import React, { useEffect, useRef } from "react";

const LiquidButton = ({ svgRef }) => {
  const options = svgRef.current.dataset;
  const id = useRef(1);
  const xmlns = "http://www.w3.org/2000/svg";
  const tension = options.tension * 1 || 0.4;
  const width = options.width * 1 || 200;
  const height = options.height * 1 || 50;
  const margin = options.margin || 40;
  const hoverFactor = options.hoverFactor || -0.1;
  const gap = options.gap || 5;
  const debug = options.debug || false;
  const forceFactor = options.forceFactor || 0.2;
  const color1 = options.color1 || "#36DFE7";
  const color2 = options.color2 || "#8F17E1";
  const color3 = options.color3 || "#BF09E6";
  const textColor = options.textColor || "#000000";
  const text = options.text || "Button";

  const layers = useRef([
    {
      points: [],
      viscosity: 0.5,
      mouseForce: 100,
      forceLimit: 2,
    },
    {
      points: [],
      viscosity: 0.8,
      mouseForce: 150,
      forceLimit: 3,
    },
  ]);

  const wrapperElement = options.wrapperElement || document.body;
  const svgTextRef = useRef(null);
  const svgDefsRef = useRef(null);
  const touches = useRef([]);
  const noise = options.noise || 0;

  useEffect(() => {
    const svg = svgRef.current;
    const svgText = svgTextRef.current;
    const svgDefs = svgDefsRef.current;

    const touchHandler = (e) => {
      touches.current = [];
      const rect = svg.getBoundingClientRect();
      for (
        let touchIndex = 0;
        touchIndex < e.changedTouches.length;
        touchIndex++
      ) {
        const touch = e.changedTouches[touchIndex];
        const x = touch.pageX - rect.left;
        const y = touch.pageY - rect.top;
        if (x > 0 && y > 0 && x < 200 && y < 100) {
          touches.current.push({ x, y, force: touch.force || 1 });
        }
      }
      e.preventDefault();
    };

    const clearHandler = (e) => {
      touches.current = [];
    };

    svg.addEventListener("mouseout", clearHandler);
    document.body.addEventListener("touchstart", touchHandler);
    document.body.addEventListener("touchmove", touchHandler);
    document.body.addEventListener("touchend", clearHandler);
    document.body.addEventListener("touchcancel", clearHandler);

    return () => {
      svg.removeEventListener("mouseout", clearHandler);
      document.body.removeEventListener("touchstart", touchHandler);
      document.body.removeEventListener("touchmove", touchHandler);
      document.body.removeEventListener("touchend", clearHandler);
      document.body.removeEventListener("touchcancel", clearHandler);
    };
  }, []);

  useEffect(() => {
    if (svgDefsRef.current && touches.current.length > 0) {
      while (svgDefsRef.current.firstChild) {
        svgDefsRef.current.removeChild(svgDefsRef.current.firstChild);
      }
      for (
        let touchIndex = 0;
        touchIndex < touches.current.length;
        touchIndex++
      ) {
        const touch = touches.current[touchIndex];
        const gradient = document.createElementNS(xmlns, "radialGradient");
        gradient.id = `liquid-gradient-${id.current}`;
        const startColor = document.createElementNS(xmlns, "stop");
        startColor.setAttribute("offset", "0%");
        startColor.setAttribute("stop-color", color2);
        const stopColor = document.createElementNS(xmlns, "stop");
        stopColor.setAttribute("offset", "100%");
        stopColor.setAttribute("stop-color", color3);
        gradient.appendChild(startColor);
        gradient.appendChild(stopColor);
        svgDefsRef.current.appendChild(gradient);

        layers.current.forEach((layer) => {});
      }
    }
  }, [
    color1,
    color2,
    color3,
    textColor,
    width,
    height,
    hoverFactor,
    margin,
    gap,
    debug,
    forceFactor,
    noise,
    text,
  ]);

  return (
    <div className="liquid-button-wrapper">
      <svg
        ref={svgRef}
        xmlns={xmlns}
        width={width}
        height={height}
        viewBox={`0 0 ${width} ${height}`}
      >
        <defs ref={svgDefsRef}></defs>
        <text
          ref={svgTextRef}
          x="50%"
          y="50%"
          dominantBaseline="middle"
          textAnchor="middle"
        >
          {text}
        </text>
      </svg>
    </div>
  );
};

export default LiquidButton;
