import React, { useState } from "react";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import css from "../../main.module.css";
import { PageOne } from "../Game-page/pageOne";
import { useDrag, useDrop } from "react-dnd";

export const DragAndDrop = () => {
  const [selectedId, setSelectedId] = useState(null);
  const [itemPosition, setItemPosition] = useState({ left: 0, top: 0 });

  const handleOnClick = (e) => {
    const id = e.target.id;
    console.log(id);
    setSelectedId(id);
  };

  function DraggableComponent() {
    const [{ isDragging }, drag] = useDrag(() => ({
      type: "BOX",
      collect: (monitor) => ({
        isDragging: monitor.isDragging(),
      }),
    }));

    return <div ref={drag}>Перетаскиваемый элемент</div>;
  }

  function DroppableComponent() {
    const [{ canDrop, isOver }, drop] = useDrop(() => ({
      accept: "BOX",
      drop: (item, monitor) => {
        const delta = monitor.getDifferenceFromInitialOffset();
        const left = Math.round(item.left + delta.x);
        const top = Math.round(item.top + delta.y);
        // Обновление позиции элемента
        // Например, сохранение новых координат в состояние или передача данных на сервер

        // Пример обновления позиции в состоянии
        setItemPosition({ left, top });
      },
      collect: (monitor) => ({
        canDrop: monitor.canDrop(),
        isOver: monitor.isOver(),
      }),
    }));

    return (
      <div
        ref={drop}
        style={{
          backgroundColor: isOver ? "green" : "white",
          position: "absolute",
          left: itemPosition.left,
          top: itemPosition.top,
        }}
      >
        Зона для сбрасывания элемента
      </div>
    );
  }

  const renderContent = () => {
    if (selectedId === "back") {
      return <PageOne />;
    } else {
      return (
        <>
          <button className={css.BackBtn} onClick={handleOnClick} id="back">
            Back
          </button>
          <div style={{ width: "100vw", height: "100vh", display: "flex" }}>
            <DraggableComponent />
            <DroppableComponent />
          </div>
        </>
      );
    }
  };

  return <DndProvider backend={HTML5Backend}>{renderContent()}</DndProvider>;
};
