import React, { useState } from "react";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import css from "../../main.module.css";
import { PageOne } from "../Game-page/pageOne";
import { useDrag, useDrop } from "react-dnd";

export const DragAndDrop = () => {
  const [selectedId, setSelectedId] = useState(null);
  const [itemPosition, setItemPosition] = useState({ left: 200, top: 500 });

  const handleOnClick = (e) => {
    const id = e.target.id;
    console.log(id);
    setSelectedId(id);
  };

  function Box() {
    const [{ isDragging }, drag, dragPreview] = useDrag(() => ({
      // "type" is required. It is used by the "accept" specification of drop targets.
      type: "BOX",
      // The collect function utilizes a "monitor" instance (see the Overview for what this is)
      // to pull important pieces of state from the DnD system.
      collect: (monitor) => ({
        isDragging: monitor.isDragging(),
      }),
    }));

    return (
      <div ref={dragPreview} style={{ opacity: isDragging ? 0.5 : 1 }}>
        <div role="Handle" ref={drag} />
      </div>
    );
  }

  function Bucket() {
    const [{ canDrop, isOver }, drop] = useDrop(() => ({
      // The type (or types) to accept - strings or symbols
      accept: "BOX",
      // Props to collect
      collect: (monitor) => ({
        isOver: monitor.isOver(),
        canDrop: monitor.canDrop(),
      }),
    }));

    return (
      <div
        ref={drop}
        role={"Dustbin"}
        style={{ backgroundColor: isOver ? "red" : "white" }}
      >
        {canDrop ? "Release to drop" : "Drag a box here"}
      </div>
    );
  }

  const renderContent = () => {
    if (selectedId === "back") {
      return <PageOne />;
    } else {
      return (
        <>
          <button className={css.BackBtn} onClick={handleOnClick} id="back">
            Back
          </button>
          <div style={{ width: "100vw", height: "100vh", display: "flex" }}>
            <DraggableComponent />
            <DroppableComponent />
          </div>
        </>
      );
    }
  };

  return <DndProvider backend={HTML5Backend}>{renderContent()}</DndProvider>;
};
