import React, { useState } from "react";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd";
import { useDrag, useDrop } from "react-dnd";

const DragAndDrop = () => {
  const [items, setItems] = useState([
    { id: 1, name: "Элемент 1" },
    { id: 2, name: "Элемент 2" },
    { id: 3, name: "Элемент 3" },
  ]);

  const handleDrop = (draggedItem, targetItem) => {
    const updatedItems = items.map((item) => {
      if (item.id === draggedItem.id) {
        // Заменяем перетаскиваемый элемент целевым элементом
        return targetItem;
      } else if (item.id === targetItem.id) {
        // Заменяем целевой элемент перетаскиваемым элементом
        return draggedItem;
      }
      return item;
    });

    setItems(updatedItems);
  };

  const DraggableComponent = ({ item }) => {
    const [{ isDragging }, drag] = useDrag(() => ({
      type: "BOX",
      item: { id: item.id, name: item.name },
      collect: (monitor) => ({
        isDragging: monitor.isDragging(),
      }),
    }));

    return (
      <div ref={drag} style={{ opacity: isDragging ? 0.5 : 1 }}>
        {item.name}
      </div>
    );
  };

  const DroppableComponent = ({ item }) => {
    const [{ isOver }, drop] = useDrop(() => ({
      accept: "BOX",
      drop: (draggedItem) => {
        handleDrop(draggedItem, item);
      },
      collect: (monitor) => ({
        isOver: monitor.isOver(),
      }),
    }));

    return (
      <div ref={drop} style={{ backgroundColor: isOver ? "green" : "white" }}>
        {item.name}
      </div>
    );
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div>
        {items.map((item) => (
          <DraggableComponent key={item.id} item={item} />
        ))}
      </div>
      <div>
        {items.map((item) => (
          <DroppableComponent key={item.id} item={item} />
        ))}
      </div>
    </DndProvider>
  );
};

export default DragAndDrop;
