import React, { useEffect, useRef } from "react";

export const LiquidButton = ({ onClick }) => {
  useEffect(() => {
    initButton();
    mouseSpeed();

    return () => {
      cancelAnimationFrame(rafID);
    };
  }, []);

  return <canvas ref={canvasRef} className="btn-liquid" />;
};

const canvasRef = useRef(null);
let canvas,
  context,
  pointsA,
  pointsB,
  mouseX,
  mouseY,
  relMouseX,
  relMouseY,
  mouseLastX,
  mouseLastY,
  mouseDirectionX,
  mouseDirectionY,
  mouseSpeedX,
  mouseSpeedY,
  rafID;

const points = 8;
const viscosity = 20;
const mouseDist = 70;
const damping = 0.05;
const showIndicators = false;
const buttonWidth = 200;
const buttonHeight = 100;

/**
 * Get mouse direction
 */
function mouseDirection(e) {
  if (mouseX < e.pageX) mouseDirectionX = 1;
  else if (mouseX > e.pageX) mouseDirectionX = -1;
  else mouseDirectionX = 0;

  if (mouseY < e.pageY) mouseDirectionY = 1;
  else if (mouseY > e.pageY) mouseDirectionY = -1;
  else mouseDirectionY = 0;

  mouseX = e.pageX;
  mouseY = e.pageY;

  relMouseX = mouseX - canvas.offsetLeft;
  relMouseY = mouseY - canvas.offsetTop;
}

/**
 * Get mouse speed
 */
function mouseSpeed() {
  mouseSpeedX = mouseX - mouseLastX;
  mouseSpeedY = mouseY - mouseLastY;

  mouseLastX = mouseX;
  mouseLastY = mouseY;

  setTimeout(mouseSpeed, 50);
}

/**
 * Init button
 */
function initButton() {
  // Get button
  const button = document.querySelector(".btn-liquid");

  // Create canvas
  canvas = canvasRef.current;
  context = canvas.getContext("2d");

  canvas.width = buttonWidth + 100;
  canvas.height = buttonHeight + 100;

  // Initialize variables
  pointsA = [];
  pointsB = [];
  mouseX = 0;
  mouseY = 0;
  relMouseX = 0;
  relMouseY = 0;
  mouseLastX = 0;
  mouseLastY = 0;
  mouseDirectionX = 0;
  mouseDirectionY = 0;
  mouseSpeedX = 0;
  mouseSpeedY = 0;

  // Add points
  let x = buttonHeight / 2;
  for (let j = 1; j < points; j++) {
    addPoints(x + ((buttonWidth - buttonHeight) / points) * j, 0);
  }
  addPoints(buttonWidth - buttonHeight / 5, 0);
  addPoints(buttonWidth + buttonHeight / 10, buttonHeight / 2);
  addPoints(buttonWidth - buttonHeight / 5, buttonHeight);
  for (let j = points - 1; j > 0; j--) {
    addPoints(x + ((buttonWidth - buttonHeight) / points) * j, buttonHeight);
  }
  addPoints(buttonHeight / 5, buttonHeight);
  addPoints(-buttonHeight / 10, buttonHeight / 2);
  addPoints(buttonHeight / 5, 0);

  button.addEventListener("mousemove", mouseDirection);

  renderCanvas();
}

/**
 * Add points
 */
function addPoints(x, y) {
  pointsA.push(new Point(x, y, 1));
  pointsB.push(new Point(x, y, 2));
}

/**
 * Point
 */
class Point {
  constructor(x, y, level) {
    this.x = this.ix = 50 + x;
    this.y = this.iy = 50 + y;
    this.vx = 0;
    this.vy = 0;
    this.cx1 = 0;
    this.cy1 = 0;
    this.cx2 = 0;
    this.cy2 = 0;
    this.level = level;
  }

  /**
   * Move point by velocity
   */
  move() {
    this.vx += (this.ix - this.x) / viscosity;
    this.vy += (this.iy - this.y) / viscosity;

    let dx = this.ix - relMouseX,
      dy = this.iy - relMouseY;
    let relDist = 1 - Math.sqrt(dx * dx + dy * dy) / mouseDist;

    // Move x
    if (
      (mouseDirectionX > 0 && relMouseX > this.x) ||
      (mouseDirectionX < 0 && relMouseX < this.x)
    ) {
      if (relDist > 0 && relDist < 1) {
        this.vx = (mouseSpeedX / 4) * relDist;
      }
    }
    this.vx *= 1 - damping;
    this.x += this.vx;

    // Move y
    if (
      (mouseDirectionY > 0 && relMouseY > this.y) ||
      (mouseDirectionY < 0 && relMouseY < this.y)
    ) {
      if (relDist > 0 && relDist < 1) {
        this.vy = (mouseSpeedY / 4) * relDist;
      }
    }
    this.vy *= 1 - damping;
    this.y += this.vy;
  }
}

/**
 * Render canvas
 */
function renderCanvas() {
  // rAF
  rafID = requestAnimationFrame(renderCanvas);

  // Clear scene
  context.clearRect(0, 0, canvas.width, canvas.height);

  // Move points
  for (let i = 0; i <= pointsA.length - 1; i++) {
    pointsA[i].move();
    pointsB[i].move();
  }

  // Create dynamic gradient
  const gradientX = Math.min(
    Math.max(mouseX - canvas.offsetLeft - buttonWidth / 2, 0),
    buttonWidth
  );
  const gradientY = Math.min(
    Math.max(mouseY - canvas.offsetTop - buttonHeight / 2, 0),
    buttonHeight
  );
  const distance =
    Math.sqrt(
      Math.pow(gradientX - buttonWidth / 2, 2) +
        Math.pow(gradientY - buttonHeight / 2, 2)
    ) / Math.sqrt(Math.pow(buttonWidth / 2, 2) + Math.pow(buttonHeight / 2, 2));

  const gradient = context.createRadialGradient(
    gradientX + canvas.offsetLeft,
    gradientY + canvas.offsetTop,
    300 + 300 * distance,
    gradientX + canvas.offsetLeft,
    gradientY + canvas.offsetTop,
    0
  );
  gradient.addColorStop(0, "#ff1050");
  gradient.addColorStop(1, "#ffad11");

  // Draw shapes
  const groups = [pointsA, pointsB];

  for (let j = 0; j <= 1; j++) {
    const points = groups[j];

    if (j === 0) {
      // Background style
      context.fillStyle = "#1c1c1c";
    } else {
      // Foreground style
      context.fillStyle = gradient;
    }

    context.beginPath();
    context.moveTo(points[0].x, points[0].y);

    for (let i = 0; i < points.length; i++) {
      const p = points[i];
      const nextP = points[i + 1];
      const val = 30 * 0.552284749831;

      if (nextP !== undefined) {
        if (nextP.ix > p.ix && nextP.iy < p.iy) {
          p.cx1 = p.x;
          p.cy1 = p.y - val;
          p.cx2 = nextP.x - val;
          p.cy2 = nextP.y;
        } else if (nextP.ix > p.ix && nextP.iy > p.iy) {
          p.cx1 = p.x + val;
          p.cy1 = p.y;
          p.cx2 = nextP.x;
          p.cy2 = nextP.y - val;
        } else if (nextP.ix < p.ix && nextP.iy > p.iy) {
          p.cx1 = p.x;
          p.cy1 = p.y + val;
          p.cx2 = nextP.x + val;
          p.cy2 = nextP.y;
        } else if (nextP.ix < p.ix && nextP.iy < p.iy) {
          p.cx1 = p.x - val;
          p.cy1 = p.y;
          p.cx2 = nextP.x;
          p.cy2 = nextP.y + val;
        } else {
          p.cx1 = (p.x + nextP.x) / 2;
          p.cy1 = (p.y + nextP.y) / 2;
          p.cx2 = (p.x + nextP.x) / 2;
          p.cy2 = (p.y + nextP.y) / 2;

          context.bezierCurveTo(p.x, p.y, p.cx1, p.cy1, p.cx1, p.cy1);
          context.lineTo(p.x, p.y);
          context.lineTo(p.cx1, p.cy1);
          context.lineTo(p.cx2, p.cy2);
          context.lineTo(nextP.x, nextP.y);
          // } else {
          //   nextP = points[0];
          //   p.cx1 = (p.x + nextP.x) / 2;
          //   p.cy1 = (p.y + nextP.y) / 2;

          //   context.bezierCurveTo(p.x, p.y, p.cx1, p.cy1, p.cx1, p.cy1);
          // }
        }

        // context.closePath();
        context.fill();
      }

      if (showIndicators) {
        // Draw points
        context.fillStyle = "#000";
        context.beginPath();
        for (let i = 0; i < pointsA.length; i++) {
          const p = pointsA[i];

          context.rect(p.x - 1, p.y - 1, 2, 2);
        }
        context.fill();

        // Draw controls
        context.fillStyle = "#f00";
        context.beginPath();
        for (let i = 0; i < pointsA.length; i++) {
          const p = pointsA[i];

          context.rect(p.cx1 - 1, p.cy1 - 1, 2, 2);
          context.rect(p.cx2 - 1, p.cy2 - 1, 2, 2);
        }
        context.fill();
      }
    }
  }
}
