import React from "react";
import css from "../../main.module.css";
import { initializeApp } from "firebase/app";
import "firebase/compat/database"; // Импорт модуля базы данных
import { getFirestore, collection, getDocs } from "firebase/firestore";
import axios from "axios";

export const Auth = () => {
  // Инициализация Firebase
  const firebaseConfig = {
    apiKey: "AIzaSyB1mWqevmipidxWW264EnXV__YFdSkx93M",
    authDomain: "umka-77a72.firebaseapp.com",
    databaseURL: "https://umka-77a72-default-rtdb.firebaseio.com",
    projectId: "umka-77a72",
    storageBucket: "umka-77a72.appspot.com",
    messagingSenderId: "270019919476",
    appId: "1:270019919476:web:28ff114af689a2143bdb21", // Initialize Firebaseconst app = initializeApp(firebaseConfig
    // и другие настройки
  };

  const app = initializeApp(firebaseConfig);
  const db = getFirestore(app);

  const fetchData = async (e) => {
    try {
      console.log(e);
      const querySnapshot = await getDocs(collection(db, "users"));
      querySnapshot.forEach((doc) => {
        console.log(doc.id, " => ", doc.data());
      });
    } catch (error) {
      console.error("Ошибка при чтении данных:", error);
    }
  };

  const postData = (e) => {
    const emailUser = document.querySelector(".email");
    const passwordUser = document.querySelector(".password");

    const data = {
      email: emailUser.value,
      password: passwordUser.value,
    };
    try {
      axios
        .post(collection(db, "users"), data)
        .then((response) => {
          // Обработка ответа сервера
          console.log(response.data);
        })
        .catch((error) => {
          // Обработка ошибок
          console.error("Ошибка:", error);
        });
    } catch (error) {
      console.log(error);
    } finally {
      fetchData();
    }
  };

  return (
    <div className={css.authContainer}>
      <div className={css.auth}>
        <input
          type="email"
          name="email"
          id="email"
          placeholder="Email"
          className={css.email}
        />
        <input
          type="password"
          name="password"
          id="password"
          placeholder="Password"
          className={css.password}
        />
        <button onClick={postData}>Go</button>
      </div>
    </div>
  );
};
