import React, { useEffect, useRef } from "react";

const LiquidButton = () => {
  const canvasRef = useRef(null);
  let canvas,
    context,
    pointsA,
    pointsB,
    mouseX,
    mouseY,
    relMouseX,
    relMouseY,
    mouseLastX,
    mouseLastY,
    mouseDirectionX,
    mouseDirectionY,
    mouseSpeedX,
    mouseSpeedY,
    rafID;

  const points = 8;
  const viscosity = 20;
  const mouseDist = 70;
  const damping = 0.05;
  const showIndicators = false;

  /**
   * Get mouse direction
   */
  function mouseDirection(e) {
    if (mouseX < e.pageX) mouseDirectionX = 1;
    else if (mouseX > e.pageX) mouseDirectionX = -1;
    else mouseDirectionX = 0;

    if (mouseY < e.pageY) mouseDirectionY = 1;
    else if (mouseY > e.pageY) mouseDirectionY = -1;
    else mouseDirectionY = 0;

    mouseX = e.pageX;
    mouseY = e.pageY;

    relMouseX = mouseX - canvas.offsetLeft;
    relMouseY = mouseY - canvas.offsetTop;
  }

  /**
   * Get mouse speed
   */
  function mouseSpeed() {
    mouseSpeedX = mouseX - mouseLastX;
    mouseSpeedY = mouseY - mouseLastY;

    mouseLastX = mouseX;
    mouseLastY = mouseY;

    setTimeout(mouseSpeed, 50);
  }

  /**
   * Init button
   */
  function initButton() {
    // Get button
    const button = document.querySelector(".btn-liquid");
    const buttonWidth = button.offsetWidth;
    const buttonHeight = button.offsetHeight;

    // Create canvas
    canvas = canvasRef.current;
    context = canvas.getContext("2d");

    canvas.width = buttonWidth + 100;
    canvas.height = buttonHeight + 100;

    // Initialize variables
    pointsA = [];
    pointsB = [];
    mouseX = 0;
    mouseY = 0;
    relMouseX = 0;
    relMouseY = 0;
    mouseLastX = 0;
    mouseLastY = 0;
    mouseDirectionX = 0;
    mouseDirectionY = 0;
    mouseSpeedX = 0;
    mouseSpeedY = 0;

    // Add points
    let x = buttonHeight / 2;
    for (let j = 1; j < points; j++) {
      addPoints(x + ((buttonWidth - buttonHeight) / points) * j, 0);
    }
    addPoints(buttonWidth - buttonHeight / 5, 0);
    addPoints(buttonWidth + buttonHeight / 10, buttonHeight / 2);
    addPoints(buttonWidth - buttonHeight / 5, buttonHeight);
    for (let j = points - 1; j > 0; j--) {
      addPoints(x + ((buttonWidth - buttonHeight) / points) * j, buttonHeight);
    }
    addPoints(buttonHeight / 5, buttonHeight);
    addPoints(-buttonHeight / 10, buttonHeight / 2);
    addPoints(buttonHeight / 5, 0);

    button.addEventListener("mousemove", mouseDirection);

    renderCanvas();
  }

  /**
   * Add points
   */
  function addPoints(x, y) {
    pointsA.push(new Point(x, y, 1));
    pointsB.push(new Point(x, y, 2));
  }

  /**
   * Point
   */
  function Point(x, y, level) {
    this.x = this.ix = 50 + x;
    this.y = this.iy = 50 + y;
    this.vx = 0;
    this.vy = 0;
    this.cx1 = 0;
    this.cy1 = 0;
    this.cx2 = 0;
    this.cy2 = 0;
    this.level = level;
  }

  Point.prototype.move = function () {
    this.vx += (this.ix - this.x) / (viscosity * this.level);
    this.vy += (this.iy - this.y) / (viscosity * this.level);

    const dx = this.ix - relMouseX;
    const dy = this.iy - relMouseY;
    const relDist = 1 - Math.sqrt(dx * dx + dy * dy) / mouseDist;

    // Move x
    if (
      (mouseDirectionX > 0 && relMouseX > this.x) ||
      (mouseDirectionX < 0 && relMouseX < this.x)
    ) {
      if (relDist > 0 && relDist < 1) {
        this.vx = (mouseSpeedX / 4) * relDist;
      }
    }
    this.vx *= 1 - damping;
    this.x += this.vx;

    // Move y
    if (
      (mouseDirectionY > 0 && relMouseY > this.y) ||
      (mouseDirectionY < 0 && relMouseY < this.y)
    ) {
      if (relDist > 0 && relDist < 1) {
        this.vy = (mouseSpeedY / 4) * relDist;
      }
    }
    this.vy *= 1 - damping;
    this.y += this.vy;
  };

  /**
   * Render canvas
   */
  function renderCanvas() {
    // rAF
    rafID = requestAnimationFrame(renderCanvas);

    // Clear scene
    context.clearRect(0, 0, canvas.width, canvas.height);
    context.fillStyle = "#fff";
    context.fillRect(0, 0, canvas.width, canvas.height);

    // Move points
    for (let i = 0; i <= pointsA.length - 1; i++) {
      pointsA[i].move();
      pointsB[i].move();
    }

    // Create dynamic gradient
    const gradientX = Math.min(
      Math.max(mouseX - canvas.offsetLeft, 0),
      canvas.width
    );
    const gradientY = Math.min(
      Math.max(mouseY - canvas.offsetTop, 0),
      canvas.height
    );
    const distance =
      Math.sqrt(
        Math.pow(gradientX - canvas.width / 2, 2) +
          Math.pow(gradientY - canvas.height / 2, 2)
      ) /
      Math.sqrt(Math.pow(canvas.width / 2, 2) + Math.pow(canvas.height / 2, 2));

    const gradient = context.createRadialGradient(
      gradientX,
      gradientY,
      300 + 300 * distance,
      gradientX,
      gradientY,
      0
    );
    gradient.addColorStop(0, "#102ce5");
    gradient.addColorStop(1, "#E406D6");

    // Draw shapes
    const groups = [pointsA, pointsB];

    for (let j = 0; j <= 1; j++) {
      const points = groups[j];

      if (j === 0) {
        // Background style
        context.fillStyle = "#1CE2D8";
      } else {
        // Foreground style
        context.fillStyle = gradient;
      }

      context.beginPath();
      context.moveTo(points[0].x, points[0].y);

      for (let i = 0; i < points.length; i++) {
        const p = points[i];
        const nextP = points[i + 1];
        const val = 30 * 0.552284749831;

        if (nextP !== undefined) {
          // if (!nextP.ix && !nextP.iy) {
          //   nextP.ix = nextP.x;
          //   nextP.iy = nextP.y;
          // }

          if (i === 0) {
            p.cx1 = (nextP.x - p.x) / 2;
            p.cy1 = (nextP.y - p.y) / 2;
          } else if (i === points.length - 1) {
            p.cx1 = (p.x - points[i - 1].x) / 2;
            p.cy1 = (p.y - points[i - 1].y) / 2;
          } else {
            p.cx1 = (nextP.x - points[i - 1].x) / 2;
            p.cy1 = (nextP.y - points[i - 1].y) / 2;
          }

          p.x += p.vx;
          p.y += p.vy;
          p.vx += (p.ix - p.x) / (viscosity * p.level);
          p.vy += (p.iy - p.y) / (viscosity * p.level);
          context.bezierCurveTo(
            p.x + p.cx1,
            p.y + p.cy1,
            p.ix - p.cx1,
            p.iy - p.cy1,
            p.ix,
            p.iy
          );
        }
      }

      context.closePath();
      context.fill();
    }

    if (showIndicators) {
      // Draw points
      context.fillStyle = "#000";
      context.beginPath();

      for (let i = 0; i < pointsA.length; i++) {
        const p = pointsA[i];

        context.rect(p.x - 1, p.y - 1, 2, 2);
      }

      context.closePath();
      context.fill();

      // Draw controls
      context.fillStyle = "#fff";
      context.beginPath();

      for (let i = 0; i < pointsA.length; i++) {
        const p = pointsA[i];

        context.rect(p.ix - 2, p.iy - 2, 4, 4);
      }

      context.closePath();
      context.fill();
    }
  }

  useEffect(() => {
    initButton();

    // Handle window resize
    window.addEventListener("resize", initButton);

    // Handle mousemove
    window.addEventListener("mousemove", mouseSpeed);

    return () => {
      // Cleanup
      cancelAnimationFrame(rafID);
      window.removeEventListener("resize", initButton);
      window.removeEventListener("mousemove", mouseSpeed);
    };
  }, []);

  return <canvas ref={canvasRef} className="liquid-button" />;
};

export default LiquidButton;
